import {ref} from "vue";

export function useField(field){
    const valid = ref(true)
    const value = ref(field.value)

    const ressign = (val) => {
        Object.keys(field.validators ?? {}).map(name =>{
            const isValid = field.validators[name](val)
            errors[name] = !isValid
        })
    }

    watch(value, ressign)
    ressign(field.value)
    return {value, valid}
}
