import {computed, watch} from "vue";

export function useAnimationSubmit(changeInput){
    const view = computed(()=>{
        if(changeInput) return {display: 'inline-block'}
        else return {display: 'none'}
    })
    const noview = computed( () => {
        console.log('novieW working!!!')
        if(changeInput) return {display: 'none'}
        else return {display: 'inline-block'}
    })
    const green = computed( () =>{
        if(changeInput) return {
            'border-color' : 'rgba(0, 255, 0, 0.4)',
            'background-color': 'rgba(0, 255, 0, 0.3)',
        }
    }
)
    watch(changeInput, view)
    watch(changeInput, noview)
    watch(changeInput, green)
    return {view, noview, green}
}
